        <style>
            body .preloader {
                background-color: #1d8fbd;
            }

            body.loaded_hiding .preloader {
                background-color: #1d8fbd;
            }

            body.loaded .preloader {
                background-color: transparent;
            }

            .preloader {
                position: fixed;
                left: 0;
                top: 0;
                right: 0;
                bottom: 0;
                z-index: 1001;
                height: 100%;
                width: 100%;
                background-color: #1d8fbd;
                transition: all 1s ease;
            }

            .preloader .logo svg {
                stroke: #ffffff;
                stroke-width: 1px;
                stroke-dasharray: 1000;
                stroke-dashoffset: 1000;
                fill: transparent;
                animation: dash 3.5s ease-out alternate 1;
            }

            .preloader .logo {
                margin-top: -10.5rem;
                animation: logoanim 1.5s ease-out forwards 1;
                position: absolute;
                left: calc(50vw - 410px);
                z-index: 5;
                stroke: #fff;
                stroke-width: 1px;
            }

            body.loaded.fin .preloader {
                display: none;
            }

            .ramka {
                position: absolute;
                top: 0px;
                left: 0px;
                z-index: 10;
                width: 100%;
                height: auto;
                opacity: 1;
                transform: translateX(20px) translateY(-20px);
            }

            .ramka rect {
                stroke: #1D8FBD;
                stroke-width: 1px;
                stroke-dasharray: 3400;
                stroke-dashoffset: 6800;
                fill: transparent;
                animation: dash2 10s ease forwards infinite;
            }

            @keyframes dash2 {
                0% {
                    stroke-dashoffset: 3400;
                }
                40% {
                    stroke-dashoffset: 6800;
                }
                80% {
                    stroke-dashoffset: 0;
                }
                100% {
                    stroke-dashoffset: 3400;
                }
            }

            @keyframes dash {
                0% {
                    stroke-dashoffset: 1000;
                    opacity: 1;
                }
                70% {
                    stroke-dashoffset: 0;
                    opacity: 1;
                }
                100% {
                    opacity: 0;
                    stroke-dashoffset: 0;
                }
            }

            @keyframes logoanim {
                0% {
                    transform: translateY(6rem) scale(1);
                    opacity: 1;
                }
                50% {
                    transform: translateY(0rem) scale(1);
                    opacity: 1;
                }
                100% {
                    opacity: 0;
                    transform: translateY(0rem) scale(3);
                }
            }
        </style>
        <script>
            window.onload = function() {
                document.body.classList.add('loaded_hiding');
                window.setTimeout(function() {
                    document.body.classList.add('loaded');
                    document.body.classList.remove('loaded_hiding');
                }, 500);
                window.setTimeout(function() {
                    document.body.classList.add('fin');
                }, 1000);
            }
        </script>



